import base64
from pydub import AudioSegment
import pyaudio
import os

# enc переменная: в нее записан звук уведомления в формате base64

enc = '''

'''

def play_sound(soundB64, tempName='temp.mp3'):
    decoded_data = base64.b64decode(soundB64)
    
    # Сохранение в файле с расширением .mp3
    with open(tempName, "wb") as f:
        f.write(decoded_data)

    sound = AudioSegment.from_mp3(tempName)
    # Получение данных звука в формате raw
    raw_data = sound.raw_data
    
    # Получение параметров звука
    sample_width = sound.sample_width
    frame_rate = sound.frame_rate
    channels = sound.channels
    
    # Воспроизведение звука
    p = pyaudio.PyAudio()
    stream = p.open(format=p.get_format_from_width(sample_width),
                    channels=channels,
                    rate=frame_rate,
                    output=True)
    stream.write(raw_data)
    stream.stop_stream()
    stream.close()
    p.terminate()
    #Удаление временного файла звука
    os.remove(tempName)



play_sound(enc)
